package javapackage;

public class Sampleifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a= 4;
		int b = 4;
		
		if(a*b == 17)
		{
			System.out.println("The even number is: "+a);
		}
		else
		{
		System.out.println("The odd number is: "+a);
		}
	}

}
